mapper.insert:
insert into admin_permission_info (id, permission_name, description, 
  permission_path, permission_type, sort_num, 
  is_delete, create_time, create_user_id, 
  update_time, update_user_id, pid
  )
values (#{id,jdbcType=BIGINT}, #{permissionName,jdbcType=VARCHAR}, #{description,jdbcType=VARCHAR}, 
  #{permissionPath,jdbcType=VARCHAR}, #{permissionType,jdbcType=INTEGER}, #{sortNum,jdbcType=INTEGER}, 
  #{isDelete,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP}, #{createUserId,jdbcType=BIGINT}, 
  #{updateTime,jdbcType=TIMESTAMP}, #{updateUserId,jdbcType=BIGINT}, #{pid,jdbcType=BIGINT}
  )
